package slacroix

import kotlin.test.Test
import kotlin.test.*

class AppTest {
    @Test
    fun test_getChars() {
        val array = ByteArray(32)
        val buf = ByteBuf(array)
        getChars(1, buf)
        assertEquals(1, buf.size)

        getChars(12, buf)
        assertEquals(2, buf.size)

        getChars(123, buf)
        assertEquals(3, buf.size)

    }
}